import React, { Fragment, useState, useEffect } from "react";
import { animated, useSpring } from "react-spring";
import "./App.css";
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Slider from '@material-ui/core/Slider';
import Typography from '@material-ui/core/Typography';
import parse from 'html-react-parser';
import NavBar from 'react-responsive-menubar/lib/NavBar';

import Main from './Main';

function App() {



  return (
    <div className="App">
      <header className="header" style={{position:"relative"}}>
          <NavBar 
          logo={'https://i.hizliresim.com/fcgbduc.png'} 
          showSideNav={false} 
          logoStyles={{height:"70px", width:"100px", float: "left"}} 
          navBarStyles={{boxShadow:"none",}} 
          linkStyles={{color:"green", fontWeight:"bold", float: "right"}}>

              <a href="/yemek">Yemekler</a>
              <a href="/icecek">Tatlılar</a>
              <a href="/tatlı">İçecekler</a>

          </NavBar>
      </header>
      <Main />
    </div>
  );
}

export default App;
