import React from "react";
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css'
import "../App.css";

const slideImages = [
  'https://i.hizliresim.com/qhwqsfk.jpg',
  'https://i.hizliresim.com/335w9kf.png',
  'https://i.hizliresim.com/1knekz4.jpg'
];


function App() {
  return (
    <div className="slide-container">
        <Slide>
          <div className="each-slide">
            <div style={{'backgroundImage': `url(${slideImages[0]})`, height: "600px", offset: "100px"}}>
            </div>
          </div>
          <div className="each-slide">
            <div style={{'backgroundImage': `url(${slideImages[1]})`, height: "600px"}}>
            </div>
          </div>
          <div className="each-slide">
            <div style={{'backgroundImage': `url(${slideImages[2]})` ,height: "600px"}}>
            </div>
          </div>
        </Slide>
        <div>&ensp;</div><div>&ensp;</div>
        <div>
            <a><a href="/yemek"><button className="button1">Yemekler</button></a>&ensp;&ensp;&ensp;
            <a href="/tatli"><button className="button1">Tatlılar</button></a>&ensp;&ensp;&ensp;<a href="/icecek"><button className="button1">İçecekler</button></a></a>
        </div>
      </div>
  );
}


export default App;
