import React, { Fragment, useState, useEffect } from "react";
import { animated, useSpring } from "react-spring";
import "./App.css";
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Slider from '@material-ui/core/Slider';
import Typography from '@material-ui/core/Typography';


var storage = window.localStorage;

const DEFAULT = "default_list";
let x = 0;
let checkBoxes = [false, false, false, false, false, false];
let checkList = ["", "", "", "", "", ""];

let maksimumYemek = 4;

// const red = "#BA181B";
const red = "#000000";
const triangle = "#2C302E"
const wheel = "#FFFFFF"
const fieldcolor = "#FFFFFF"
const textcolor = "#FFFFFF"


let default_list = JSON.parse(storage.getItem(DEFAULT)) ?? [
  "aa",
  "bb",
  "cc",
  "dd",
  "ee",
];

const OFFSET = Math.random();

const map = function (value, in_min, in_max, out_min, out_max) {
  if (value === 0) {
    return out_min;
  }
  return ((value - in_min) * (out_max - out_min)) / (in_max - in_min) + out_min;
};

function App() {
  const r = 200;
  const cx = 250;
  const cy = 250;
  const [list, setList] = useState(default_list);
  getWinner();
  if (x == 0) {
    const params = { filters: "", count: maksimumYemek + ""};
    axios.post('http://localhost:8080/filters', params)
      .then(function (response) {

        default_list = response.data.toString().split("/");

        setList(default_list);
        x = 1;
      });
  }
  const [power, setPower] = useState(0);
  const [acc, setAcc] = useState(0);
  const config = { mass: 50, tension: 200, friction: 200, precision: 0.001 };
  const [props, set] = useSpring(() => ({
    transform: "rotate(0deg)",
    immediate: false,
  }));

  useEffect(()=>{

  },[props])

  useEffect(() => {

    set({
      from: { transform: `rotate(${map(acc, 0, 100, 0, 1700)}deg)` },
      transform: `rotate(${map(acc + power, 0, 100, 0, 1700)}deg)`,
      immediate: false,
      config,
    });
    setAcc(acc + power);
  }, [power]);

  const rederItems = (numOfItems) => {
    let items = [];
    for (let i = 0; i < numOfItems; i++) {
      let xLength = Math.cos(2 * Math.PI * (i / numOfItems + OFFSET)) * (r - 5);
      let yLength = Math.sin(2 * Math.PI * (i / numOfItems + OFFSET)) * (r - 5);
      let txLength =
        Math.cos(2 * Math.PI * ((i + 0.5) / numOfItems + OFFSET)) * (r / 2);
      let tyLength =
        Math.sin(2 * Math.PI * ((i + 0.5) / numOfItems + OFFSET)) * (r / 2);
      items.push(
        <Fragment key={i}>
          <line
            stroke={red}
            strokeWidth="2"
            x1={cx + xLength}
            y1={cy + yLength}
            x2={cx}
            y2={cy}
          />
          <text
            className="textstyle2"
            x={cx + txLength - 50}
            y={cy + tyLength}
            fontSize="15px"
            transform={`rotate(${((i + 0.5) / numOfItems + OFFSET) * 360} 
                  ${cx + txLength},
                  ${cy + tyLength})`}
          >
            {list[i]}
          </text>
        </Fragment>
      );
    }
    return items;
  };


  return (
    <div>
    <div><h1>Bugün Ne Yesem?</h1></div>
    <div style={{ display: "grid", gridTemplateColumns: "0px 500px", gridGap: 20 }}>

      <fieldset style={{backgroundColor:fieldcolor}}>
        <legend className="textstyle"> Filtreler </legend>
        <div className="textstyle">Aşağıdan yemeğinizin içerisinde bulunmasını istemediğiniz ürünleri çıkartıp "Yemekleri Yenile" butonuna basarak tekerleği güncelleyebilirsiniz!</div>
        <div>
          <input type="checkbox" id="yemek1" name="ingredient1" value="yemek1" onChange={() => {
            checkBoxes[0] = !checkBoxes[0];
            if (checkBoxes[0]) {
              checkList[0] = "et";
            }
            else {
              checkList[0] = "";
            }
          }} />
          <label className="textstyle" for="yemek1">Et</label>
        </div>
        <div>
          <input type="checkbox" id="yemek2" name="ingredient2" value="yemek2" onChange={() => {
            checkBoxes[1] = !checkBoxes[1];
            if (checkBoxes[1]) {
              checkList[1] = "balık";
            }
            else {
              checkList[1] = "";
            }
          }} />
          <label className="textstyle" for="yemek2">Balık</label>
        </div>
        <div>
          <input type="checkbox" id="yemek3" name="ingredient3" value="yemek3" onChange={() => {
            checkBoxes[2] = !checkBoxes[2];
            if (checkBoxes[2]) {
              checkList[2] = "tavuk";
            }
            else {
              checkList[2] = "";
            }
          }} />
          <label className="textstyle" for="yemek3">Tavuk</label>
        </div>
        <div>
          <input type="checkbox" id="yemek3" name="ingredient3" value="yemek3" onChange={() => {
            checkBoxes[3] = !checkBoxes[3];
            if (checkBoxes[3]) {
              checkList[3] = "bulgur";
            }
            else {
              checkList[3] = "";
            }
          }} />
          <label className="textstyle" for="yemek3">Bulgur</label>
        </div>
        <div>
          <input type="checkbox" id="yemek4" name="ingredient4" value="yemek4" onChange={() => {
            checkBoxes[4] = !checkBoxes[4];
            if (checkBoxes[4]) {
