import React, {useState} from "react";
import "./App.css";
import 'react-toastify/dist/ReactToastify.css';
import NavBar from 'react-responsive-menubar/lib/NavBar';

import Main from './Main';



function App() {

  const [index, setIndex] = useState(0);
  const [ready, setReady] = useState(false);

  return (
    <div className="App">
      <header className="header" style={{backgroundColor: "white"}}>
          <NavBar 
          logo={'https://i.hizliresim.com/fcgbduc.png'} 
          showSideNav={false} 
          logoStyles={{  height:"0px", width:"0px"}} 
          navBarStyles={{float: "middle"}} 
          linkStyles={{color:"blue", fontWeight:"bold",}}>

              <a className="navbarheader" href="/" ><img src='https://i.hizliresim.com/74gzxwf.jpg' style={{height:"50px", width:"70px"}}></img></a>
              <a onClick={()=>{ if (ready) setIndex(1)}} className="navbarheader" href="/yemek">Yemekler</a>
              <a onClick={()=>{ if (ready) setIndex(2)}} className="navbarheader" href="/tatli">Tatlılar</a>
              <a onClick={()=>{ if (ready) setIndex(3)}} className="navbarheader" href="/icecek">İçecekler</a>
              <a onClick={()=>{ if (ready) setIndex(4)}} className="navbarheader" href="/support">İletişim</a>
              <a onClick={()=>{ if (ready) setIndex(5)}} className="navbarheader" href="/icecek"></a>

          </NavBar>
      </header>
      <Main />
    </div>
  );
}

export default App;
