import React from "react";
import "../support.css";
import Grid from '@material-ui/core/Grid';
import { ToastContainer, toast } from 'react-toastify';

function App() {
    return (
        <div>
            <div>&ensp;</div><div>&ensp;</div>
            <header>Herhangi bir hatayla karşılaştığınızda veya önerilerinizi paylaşmak amacıyla bize ulaşmak için <b>dogukanterzi@gmail.com</b> <br></br> adresine mail atabilir ya da aşağıdaki alanı kullanarak bize mesaj gönderebilirsiniz!</header>
            <div>&ensp;</div><div>&ensp;</div>
            <textarea placeholder="Buraya yazabilirsiniz" id="textAreaID" style={{ width: "300px", height: "300px" }}>
            </textarea>
            <div>&ensp;</div><div>&ensp;</div>
            <button className="gonderbutton" onClick={buttonClickEvent}>Gönder!</button>
            <ToastContainer />
        </div>
    );
}

const buttonClickEvent = () => {
    const innerText = document.getElementById("textAreaID").innerHTML;
    if (innerText.value.length == 0){
        toast.warning("Lütfen alana geri bildiriminizi yazdıktan sonra \"Gönder\" tuşuna basınız.");
    }
    else{
        toast.success("Geri bildiriminiz için teşekkür ederiz!");
    }
}



export default App;
