import React, { Fragment, useState, useEffect } from "react";
import { animated, useSpring } from "react-spring";
import { ReactComponent as Logo } from "./delete.svg";
import "./App.css";
import axios from 'axios';
import { Container, Row, Col } from 'react-bootstrap';

var storage = window.localStorage;

const DEFAULT = "default_list";
let x = 0;

let default_list = JSON.parse(storage.getItem(DEFAULT)) ?? [
  "aa",
  "bb",
  "cc",
  "dd",
  "ee",
];

const OFFSET = Math.random();

const map = function (value, in_min, in_max, out_min, out_max) {
  //console.log(value);
  //console.log(
  //  ((value - in_min) * (out_max - out_min)) / (in_max - in_min) + out_min
  //);
  if (value === 0) {
    return out_min;
  }
  return ((value - in_min) * (out_max - out_min)) / (in_max - in_min) + out_min;
};

function App() {
  const r = 200;
  const cx = 250;
  const cy = 250;
  const [list, setList] = useState(default_list);

  if (x == 0) {

    console.log(x);
    const params = { filters: '', count: '2' };
    axios.post('http://localhost:8080/filters', params)
      .then(function (response) {

        default_list = response.data.toString().split("/");

        setList(default_list);
        x = 1;
      });


  }
  const [power, setPower] = useState(0);
  const [acc, setAcc] = useState(0);
  const config = { mass: 50, tension: 200, friction: 200, precision: 0.001 };
  const [props, set] = useSpring(() => ({
    transform: "rotate(0deg)",
    immediate: false,
  }));
  // const addItem = () => {
  //   storage.setItem(DEFAULT, JSON.stringify([...list, name]));
  //   setList([...list, name]);
  //   setName("");
  // };
  // const deleteItem = (e) => {
  //   const { item } = e.currentTarget.dataset;
  //   console.log(item)
  //   storage.setItem(DEFAULT, JSON.stringify(list.filter((e) => e !== item)));
  //   setList(list.filter((e) => e !== item));
  // };
  // const reset=()=>{
  //   storage.clear()
  //   window.location.reload()
  // }

  useEffect(() => {
    set({
      from: { transform: `rotate(${map(acc, 0, 100, 0, 1700)}deg)` },
      transform: `rotate(${map(acc + power, 0, 100, 0, 1700)}deg)`,
      immediate: false,
      config,
    });
    setAcc(acc + power);
  }, [power]);

  const rederItems = (numOfItems) => {
    let items = [];
    for (let i = 0; i < numOfItems; i++) {
      let xLength = Math.cos(2 * Math.PI * (i / numOfItems + OFFSET)) * (r - 5);
      let yLength = Math.sin(2 * Math.PI * (i / numOfItems + OFFSET)) * (r - 5);
      let txLength =
        Math.cos(2 * Math.PI * ((i + 0.5) / numOfItems + OFFSET)) * (r / 2);
      let tyLength =
        Math.sin(2 * Math.PI * ((i + 0.5) / numOfItems + OFFSET)) * (r / 2);
      items.push(
        <Fragment key={i}>
          <line
            stroke="rgb(255,0,0)"
            strokeWidth="2"
            x1={cx + xLength}
            y1={cy + yLength}
            x2={cx}
            y2={cy}
          />
          <text
            x={cx + txLength}
            y={cy + tyLength}
            fontSize="15px"
            transform={`rotate(${((i + 0.5) / numOfItems + OFFSET) * 360} 
                  ${cx + txLength},
                  ${cy + tyLength})`}
          >
            {list[i]}
          </text>
        </Fragment>
      );
    }
    return items;
  };

  return (
    <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 200px)", gridGap: 20 }}>
      <fieldset>
        <legend> Yemeğimde Neler Olmasın? </legend>
        <div>
          <input type="checkbox" id="yemek1" name="ingredient1" value="yemek1"/>
          <label for="yemek1">Et</label>
        </div>
        <div>
          <input type="checkbox" id="yemek2" name="ingredient2" value="yemek2"/>
          <label for="yemek2">Balık</label>
        </div>
      </fieldset>
      <div>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 500 500"
          style={{ width: "100vw", height: "80vh" }}
        >
          <g fill="white" stroke="green" strokeWidth="10">
            <circle cx="250" cy="250" r={r} />
          </g>
          <animated.g
            style={{
              transform: props.transform,
              transformOrigin: "center",
            }}
          >
            {rederItems(list.length)}
          </animated.g>
          <g fill="#61DAFB">
            <circle cx="250" cy="250" r="15" />
          </g>
          <g fill="black">
            <circle cx="250" cy="250" r="5" />
          </g>
          <g fill="lime" stroke="purple" strokeWidth="2">
            <polygon points="250,70 230,30 270,30" />
          </g>
        </svg>
        <button className="main">Yemekleri Yenile</button>
        <PressButton setPower={setPower} style={{ height: "20vh" }} />
      </div>

    </div>
  );
}

const PressButton = ({ setPower }) => {
  const [pressed, toggle] = useState(false);
  const [width, setWidth] = useState(0);
  const [props, set] = useSpring(() => ({
    width: "0%",
    backgroundColor: "hotpink",
  }));
  useEffect(() => {
    if (pressed)
      set({
        from: { width: "0%", backgroundColor: "hotpink" },
        to: { width: "100%", backgroundColor: "red" },
        immediate: false,
        config: { duration: 2000 },
      });
    else {
      setPower(parseInt(width));
      set({ to: { width: "0%", backgroundColor: "hotpink" }, immediate: true });
    }
  }, [pressed]);

  return (
    <button
      className="main"
      onMouseDown={() => {
        toggle(!pressed);
      }}
      onMouseUp={() => {
        toggle(!pressed);
      }}
      onTouchStart={() => {
        toggle(!pressed);
      }}
      onTouchEnd={() => {
        toggle(!pressed);
      }}
    >
      <animated.div
        className="fill"
        style={{
          width: props.width,
          background: props.backgroundColor,
        }}
      />
      <animated.div className="content">
        {props.width.interpolate((x) => {
          setWidth(parseInt(x));
          return x === "0%" ? "Döndür!" : parseInt(x) + "%";
        })}
      </animated.div>
    </button>
  );
};

export default App;
